import React, { useState, useContext, useEffect } from "react";
import "./editor.scss";
import { ChromePicker, CirclePicker } from "react-color";
import DrawingPanel from "./DrawingPanel";
import ColorMatrix from "./ColorMatrix"
import axios from "axios";
import { Link } from "react-router-dom"
import { useLocation } from "react-router-dom"
import { Context } from "../../context/Context"
import { Card } from "../blog/Card";
import { print,colors } from "../../assets/data/data"

export const Editor = () => {
  const [panelWidth, setPanelWidth] = useState(16);
  const [panelHeight, setPanelHeight] = useState(16);
  const [hideOptions, setHideOptions] = useState(false);
  const [hideDrawingPanel, setHideDrawingPanel] = useState(true);
  const [buttonText, setButtonText] = useState("start drawing");
  const [selectedColor, setColor] = useState("#f44336");

  const [prints, setPrints] = useState([]);

  const { user } = useContext(Context)
  //-------------------------back-----------------------------
  const PublicFlo = "http://localhost:5000/images/"
  const { search } = useLocation()
  
  const [posts, setPosts] = useState([])
  useEffect(() => {
    const fetchPost = async () => {
      const res = await axios.get("/prints" + search)
      setPosts(res.data)
    }
    fetchPost()
  }, [search])
  //-------------------------back-----------------------------


  function initializeDrawingPanel() {
    setHideOptions(!hideOptions);
    setHideDrawingPanel(!hideDrawingPanel);

    buttonText === "start drawing"
      ? setButtonText("reset")
      : setButtonText("start drawing");
  }

  function changeColor(color) {
    setColor(color.hex);
  }

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/images/');
        setPrints(response.data);
      } catch (error) {
        console.error('Error fetching prints:', error);
      }
    };

    fetchData();
  }, []);


  // const ColorMatrix = ({ colors, width }) => {
  //   // Разбиваем строку с цветами на массив
  //   const colorArray = colors.split(' ');
  
  //   // Генерируем стили для матрицы цветов
  //   const matrixStyles = {
  //     display: 'grid',
  //     gridTemplateColumns: `repeat(${width}, 1fr)`,
  //   };
  
  //   return (
  //     <div style={matrixStyles}>
  //       {colorArray.map((color, index) => (
  //         <div
  //           key={index}
  //           style={{
  //             backgroundColor: color,
  //             height: '1rem', // Вы можете настроить высоту ячеек по своему усмотрению
  //           }}
  //         />
  //       ))}
  //     </div>
  //   );
  // };


//--------------------------------------------------------
  return (
    <div id="editor">

      <div id="tool">
        <CirclePicker color={selectedColor} onChangeComplete={changeColor} />
        <ChromePicker color={selectedColor} onChange={changeColor} />
      </div>
      
      <div id="workspace">
        <h1>Pixel Editor</h1>
        {hideDrawingPanel && <h2>Enter Panel Dimensions</h2>}
        {hideDrawingPanel && (
          <div id="options">
            <div className="option">
              <input
                type="number"
                className="panelInput"
                defaultValue={panelWidth}
                onChange={(e) => {
                  setPanelWidth(e.target.value);
                }}
              />
              <span>Width</span>
            </div>
            <div className="option">
              <input
                type="number"
                className="panelInput"
                defaultValue={panelHeight}
                onChange={(e) => {
                  setPanelHeight(e.target.value);
                }}
              />
              <span>Height</span>
            </div>
          </div>
        )}

        <button onClick={initializeDrawingPanel} className="button">
          {buttonText}
        </button>

        {hideOptions && (
          <DrawingPanel
            width={panelWidth}
            height={panelHeight}
            selectedColor={selectedColor}
          />
        )}
      </div>

      <div className="printList">
        {user ?(
            <div className="slides">
              
              <div className="clrmatr">
              {prints.map((print) => (
                <div key={print._id}>
                  <h3>Print ID: {print._id}</h3>
                  <p>Colors: {print.colors}</p>
                  <p>Width: {print.width}</p>
                </div>
              ))}
              </div>

              <div className="clrmatr">
                {colors.map((data, index) => (
                  <ColorMatrix key={index} {...data} />
                ))}
              </div>

              
            </div>
          ):(
            <button className="button">
              <Link to="/login"> to login </Link>
            </button>
          )}
      </div>
      

    </div>
  );
}
