import React, {useState , useRef, useContext, useEffect } from "react";
import "./drawingPanel.scss";
import Row from "./Row";
import { Link } from "react-router-dom"
import { useLocation } from "react-router-dom"
import { exportComponentAsPNG } from "react-component-export-image";
import axios from "axios";
import { Context } from "../../context/Context"



export default function DrawingPanel(props) {
  const { width, height, selectedColor } = props;

  const { user } = useContext(Context)

  const [file, setPrint] = useState(null)

  const panelRef = useRef();

  let rows = [];

  for (let i = 0; i < height; i++) {
    rows.push(<Row key={i} width={width} selectedColor={selectedColor} />);
  }

  // console.log(rows)
  const [backgroundColors, setBackgroundColors] = useState([]);

  useEffect(() => {
    // Функция для обхода div с id "pixels" и получения их background-color
    const getBackgroundColors = () => {
      const pixelsContainer = document.getElementById('pixels');
      if (pixelsContainer) {
        const pixels = pixelsContainer.getElementsByTagName('div');
        const colors = [];

        for (let i = 0; i < pixels.length; i++) {
          const pixel = pixels[i];
          const computedStyle = window.getComputedStyle(pixel);
          const backgroundColor = computedStyle.backgroundColor;
          colors.push(backgroundColor);
        }

        setBackgroundColors(colors);
        console.log('Background Colors:', colors); 
      }
    };

    // Вызываем функцию при монтировании компонента
    getBackgroundColors();
  }, []); 
  //

  return (
    <div id="drawingPanel">
      <div id="pixels" required ref={panelRef}>
        {rows}
      </div>
      <div id="buttons">
        <button onClick={() => exportComponentAsPNG(panelRef)} className="button">
          Export as PNG
        </button>
        {user ?(
            
              <button className="button" onClick={(e) => setPrint(e.panelRef.files[0])} >
                +
              </button>
   
        ):(        
          <button className="button">
            <Link to="/login"> to login </Link>
          </button>         
        )}
      </div>
    </div>
  );
}
