import React, { useState, useContext, useEffect } from "react";
import "./editor.scss";
import { ChromePicker, CirclePicker } from "react-color";
import DrawingPanel from "./DrawingPanel";
import axios from "axios";
import { Link } from "react-router-dom"
import { useLocation } from "react-router-dom"
import { Context } from "../../context/Context"
import { Card } from "../blog/Card";
import { print,colors } from "../../assets/data/data"

export const Editor = () => {
  const [panelWidth, setPanelWidth] = useState(16);
  const [panelHeight, setPanelHeight] = useState(16);
  const [hideOptions, setHideOptions] = useState(false);
  const [hideDrawingPanel, setHideDrawingPanel] = useState(true);
  const [buttonText, setButtonText] = useState("start drawing");
  const [selectedColor, setColor] = useState("#f44336");

  const { user } = useContext(Context)
  //-------------------------back-----------------------------
  const PublicFlo = "http://localhost:5000/images/"
  const { search } = useLocation()
  
  const [posts, setPosts] = useState([])
  useEffect(() => {
    const fetchPost = async () => {
      const res = await axios.get("/prints" + search)
      setPosts(res.data)
    }
    fetchPost()
  }, [search])
  //-------------------------back-----------------------------

  function LogPrint(){
    console.log(print)
  }

  function initializeDrawingPanel() {
    setHideOptions(!hideOptions);
    setHideDrawingPanel(!hideDrawingPanel);

    buttonText === "start drawing"
      ? setButtonText("reset")
      : setButtonText("start drawing");
  }

  function changeColor(color) {
    setColor(color.hex);
  }

  const PrintTable = ({ data }) => {
    return (
      <table>
        <tbody>
          {data.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {Object.values(row).map((cell, cellIndex) => (
                <td key={cellIndex} style={{ backgroundColor: cell }}>
                  {cell}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  const ColorComponent = ({ colors, width, height }) => {
    const colorMatrix = colors.split(' ').reduce((matrix, color, index) => {
      const rowIndex = Math.floor(index / width);
      const colIndex = index % width;
  
      if (!matrix[rowIndex]) {
        matrix[rowIndex] = [];
      }
  
      matrix[rowIndex][colIndex] = color;
      return matrix;
    }, []);
  
    return (
      <div>
        {colorMatrix.map((row, rowIndex) => (
          <div key={rowIndex} style={{ display: 'flex' }}>
            {row.map((color, colIndex) => (
              <div
                key={colIndex}
                style={{
                  backgroundColor: color,
                  width: '1rem', // You can adjust the size as needed
                  height: '1rem', // You can adjust the size as needed
                  border: '1px solid #000', // Add a border for better visibility
                }}
              ></div>
            ))}
          </div>
        ))}
      </div>
    );
  };

//--------------------------------------------------------
  return (
    <div id="editor">

      <div id="tool">
        <CirclePicker color={selectedColor} onChangeComplete={changeColor} />
        <ChromePicker color={selectedColor} onChange={changeColor} />
      </div>
      
      <div id="workspace">
        <h1>Pixel Editor</h1>
        {hideDrawingPanel && <h2>Enter Panel Dimensions</h2>}
        {hideDrawingPanel && (
          <div id="options">
            <div className="option">
              <input
                type="number"
                className="panelInput"
                defaultValue={panelWidth}
                onChange={(e) => {
                  setPanelWidth(e.target.value);
                }}
              />
              <span>Width</span>
            </div>
            <div className="option">
              <input
                type="number"
                className="panelInput"
                defaultValue={panelHeight}
                onChange={(e) => {
                  setPanelHeight(e.target.value);
                }}
              />
              <span>Height</span>
            </div>
          </div>
        )}

        <button onClick={initializeDrawingPanel} className="button">
          {buttonText}
        </button>

        {hideOptions && (
          <DrawingPanel
            width={panelWidth}
            height={panelHeight}
            selectedColor={selectedColor}
          />
        )}
      </div>

      <div className="printList">
        {user ?(
            <div className="slides">
              
              <div>
                {print.map((row, rowIndex) => (
                  <div key={rowIndex} style={{ display: 'flex' }}>
                    {Object.values(row).map((color, columnIndex) => (
                      <div key={columnIndex} style={{ backgroundColor: color, width: '1rem', height: '1rem'}}></div>
                    ))}
                  </div>
                ))}
              </div>

              <div className="clrmatr">
                <ColorComponent {...colors} />
              </div>


              {/* {posts.map((item) => (
              <div className='box boxItems' key={item.id}>
                <div className='img'>{item.photo && <img src={PublicFlo + item.photo} alt='' />}</div>
              </div>
              ))} */}
            </div>
          ):(
            <button className="button">
              <Link to="/login"> to login </Link>
            </button>
          )}
      </div>
      

    </div>
  );
}
