import React, { useState, useContext, useEffect } from "react"
import "./blog.css"
import { AiOutlineTags, AiOutlineClockCircle, AiOutlineHeart } from "react-icons/ai"
import { Link } from "react-router-dom"
import axios from "axios"
import { Context } from "../../context/Context"

export const Card = ({ posts }) => {
  const { user } = useContext(Context);

  const PublicFlo = "http://localhost:5000/images/";

  const [likes, setLikes] = useState({});
  const [isLikes, setIsLiked] = useState(false);

  useEffect(() => {
    // Fetch initial likes count
    const fetchLikes = async () => {
      const likesData = {};
      for (const post of posts) {
        const response = await axios.get(`/posts/${post._id}`);
        likesData[post._id] = response.data.likes;
      }
      setLikes(likesData);
    };

    fetchLikes();
  }, [posts]);

  const handleLikeClick = async (postId) => {
    try {
      let updatedLikes;
      
      if (likes[postId] % 2 === 0) {
        // If likes count is even, remove the last digit
        updatedLikes = Math.floor(likes[postId] / 10);
      } else {
        // If likes count is odd, increment by 1
        updatedLikes = likes[postId] + 1;
      }
  
      const response = await axios.put(`/posts/${postId}`, {
        username: user.username,
        likes: updatedLikes,
      });
  
      // Update likes count in the state
      setLikes((prevLikes) => ({
        ...prevLikes,
        [postId]: response.data.likes,
      }));
    } catch (error) {
      console.error("Error updating likes", error.message);
    }
  };
  
  return (
    <>
      <section className="blog">
        <div className="container grid3">
          {posts.map((item) => (
            <div className="box boxItems" key={item.id}>
              {/* ... (previous code) */}
              <div className="date">
                <AiOutlineClockCircle className="icon" />{" "}
                <label htmlFor="">{new Date(item.createdAt).toDateString()}</label>
                <AiOutlineHeart
                  className={`icon ${likes[item._id] % 2 === 1 ? "liked-icon" : ""}`}
                  onClick={() => handleLikeClick(item._id)}
                />{" "}
                <label htmlFor="">{String(likes[item._id]).length}</label>
              </div>
            </div>
          ))}
        </div>
      </section>
    </>
  );
};
