type Booleanish = boolean | "true" | "false"

export const dataAttr = (guard: boolean | undefined) => {
  return (guard ? "" : undefined) as Booleanish
}

export const ariaAttr = (guard: boolean | undefined) => {
  return guard ? "true" : undefined
}
export function isHTMLElement(value: any): value is HTMLElement {
  return typeof value === "object" && value?.nodeType === Node.ELEMENT_NODE && typeof value?.nodeName === "string"
}
import { isHTMLElement } from "./is-html-element"

type Target = HTMLElement | EventTarget | null | undefined

export function contains(parent: Target, child: Target) {
  if (!parent || !child) return false
  if (!isHTMLElement(parent) || !isHTMLElement(child)) return false
  return parent === child || parent.contains(child)
}

export const isSelfEvent = (event: Pick<UIEvent, "currentTarget" | "target">) =>
  contains(event.currentTarget, event.target)
type Ctx = { getRootNode?: () => Document | ShadowRoot | Node }

const getDocument = (node: Document | ShadowRoot | Node) => {
  if (node.nodeType === Node.DOCUMENT_NODE) return node as Document
  return node.ownerDocument ?? document
}

export function createScope<T>(methods: T) {
  const screen = {
    getRootNode: (ctx: Ctx) => (ctx.getRootNode?.() ?? document) as Document | ShadowRoot,
    getDoc: (ctx: Ctx) => getDocument(screen.getRootNode(ctx)),
    getWin: (ctx: Ctx) => screen.getDoc(ctx).defaultView ?? window,
    getActiveElement: (ctx: Ctx) => screen.getDoc(ctx).activeElement as HTMLElement | null,
    getById: <T extends HTMLElement = HTMLElement>(ctx: Ctx, id: string) =>
      screen.getRootNode(ctx).getElementById(id) as T | null,
  }
  return { ...screen, ...methods }
}
const isDocument = (el: any): el is Document => el.nodeType === Node.DOCUMENT_NODE

export function getDocument(el: Element | Node | Document | null) {
  if (isDocument(el)) return el
  return el?.ownerDocument ?? document
}

export function getWindow(el: HTMLElement) {
  return el?.ownerDocument.defaultView ?? window
}
export function getActiveElement(el: HTMLElement): HTMLElement | null {
  let activeElement = el.ownerDocument.activeElement as HTMLElement | null

  while (activeElement?.shadowRoot) {
    const el = activeElement.shadowRoot.activeElement as HTMLElement | null
    if (el === activeElement) break
