import React from "react";

export default function ColorMatrix ({ colors, width })  {
    // Разбиваем строку с цветами на массив
    const colorArray = colors.split(' ');
  
    // Генерируем стили для матрицы цветов
    const matrixStyles = {
      display: 'grid',
      gridTemplateColumns: `repeat(${width}, 1fr)`,
    };
  
    return (
      <div style={matrixStyles}>
        {colorArray.map((color, index) => (
          <div
            key={index}
            style={{
              backgroundColor: color,
              height: '1rem', // Вы можете настроить высоту ячеек по своему усмотрению
            }}
          />
        ))}
      </div>
    );
  };
