import React, { useState, useContext, useEffect } from "react";
import "./blog.css";
import { AiOutlineTags, AiOutlineClockCircle, AiOutlineHeart } from "react-icons/ai";
import { Link } from "react-router-dom";
import axios from "axios";
import { Context } from "../../context/Context";

export const Card = ({ posts }) => {
  const { user } = useContext(Context);

  const [likes, setLikes] = useState("");
  const [isLiked, setIsLiked] = useState(false);

  const likedPost = () => {
    if (isLiked === false) {
      setLikes(likes + 1);
      setIsLiked(true);
    } else {
      setLikes(likes - 1);
      setIsLiked(false);
    }
  };

  const handleLikeClick = async (item) => {
    likedPost();
    console.log(likes);

    try {
      await axios.put(`/posts/${item._id}`, { username: user.username, likes: likes });
      console.error('likes', item.likes);
    } catch (error) {
      console.error('hueta', error.message);
      console.error('hueta', item._id);
    }
  };

  useEffect(() => {
    // Update the 'likes' value in the 'posts' array
    const updatedPosts = posts.map((post) => {
      if (post._id === item._id) {
        return { ...post, likes: likes };
      } else {
        return post;
      }
    });

    // Assuming you have a function to update 'posts' in the parent component
    // Replace 'updatePosts' with the actual function to update the 'posts' array
    updatePosts(updatedPosts);
  }, [likes, item._id, posts, updatePosts]);

  const PublicFlo = "http://localhost:5000/images/";

  return (
    <>
      <section className="blog">
        <div className="container grid3">
          {posts.map((item) => (
            <div className="box boxItems" key={item.id}>
              <div className="img">{item.photo && <img src={PublicFlo + item.photo} alt="" />}</div>
              <div className="details">
                <div className="tag">
                  <AiOutlineTags className="icon" />
                  {item.categories.map((c) => (
                    <a href="/" key={c.name}>#{c.name}</a>
                  ))}
                </div>
                <Link to={`/post/${item._id}`}>
                  <h3>{item.title}</h3>
                </Link>
                <p>{item.desc.slice(0, 180)}...</p>
                <div className={`date ${isLiked ? 'liked' : ''}`}>
                  <AiOutlineClockCircle className="icon" />{" "}
                  <label htmlFor="">{new Date(item.createdAt).toDateString()}</label>
                  <AiOutlineHeart
                    className={`icon ${isLiked ? 'liked-icon' : ''}`}
                    onClick={() => handleLikeClick(item)}
                  />
                  <label htmlFor="">{item.likes}</label>
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>
    </>
  );
};
