import React from "react";
import "./create.css";

export const Choose = (photo) => {
    
    const handleCopyColors = () => {
        const pixels = document.querySelectorAll('.inputfile');
        colorArray.forEach((color, index) => {
          if (pixels[index]) {
            pixels[index].style.backgroundColor = color;
          }
        });
      };

    return(
        <>
            <div className="choosen">
                <img key={photo._id} src={photo.picture} alt={`pixel`} />
                <button className="button" onClick={handleCopyColors}> rem </button>
            </div>
        </>

    )
}
