import React, { useState } from 'react';
import axios from "axios";

export const Forgot = () => {
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Вы можете здесь отправить данные на бэкенд
    // Например, используя fetch или axios

    try {
      const response = await axios.fetch('/api/send-email', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, message }),
      });

      if (response.ok) {
        console.log('Email sent successfully');
        // Добавьте здесь логику для обработки успешной отправки
      } else {
        console.error('Failed to send email');
        // Добавьте здесь логику для обработки ошибки
      }
    } catch (error) {
      console.error('Error sending email', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Email:
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      </label>
      <br />
      <label>
        Message:
        <textarea value={message} onChange={(e) => setMessage(e.target.value)} />
      </label>
      <br />
      <button type="submit">Send Email</button>
    </form>
  );
};
