import React, { useState, useContext, useEffect} from "react"
import "./blog.css"
import { AiOutlineTags, AiOutlineClockCircle, AiOutlineHeart } from "react-icons/ai"
import { Link } from "react-router-dom"
import axios from "axios"
import { Context } from "../../context/Context"
import { Likes } from "./Likes"

export const Card = ({ posts }) => {

  const { user } = useContext(Context)
  const [likes, setLikes] = useState(0);
  const [isLiked, setIsLiked] = useState(false);
  const [isItem, setIsItem] = useState();

  const { search } = `/posts/${isItem._id}`
  
  useEffect(() => {
    const fetchPost = async () => {
      const res = await axios.get(search)
      setLikes(res.likes)
    }
    fetchPost()
  }, [search])

  const Iteminator = (item) => {
    setIsItem(item)
    handleLikeClick()
  }

  const likedPost = () => {
    if (!isLiked) {
      setLikes(likes + 1);
      setIsLiked(true);
    } else {
      setLikes(likes - 1);
      setIsLiked(false);
    }
  } 
  const handleLikeClick = async () => {
    likedPost();
    console.log(likes)

    try {
      await axios.put(`/posts/${isItem._id}`, { username: user.username, likes: likes });
      console.error('likes', isItem.likes);
    } catch (error) {
      console.error('hueta', error.message);
      console.error('hueta', isItem._id);
    }
  };

  const PublicFlo = "http://localhost:5000/images/"
  return (
    <>
      <section className='blog'>
        <div className='container grid3'>
          {posts.map((item) => (
            <div className='box boxItems' key={item.id}>
              {/* first ma yo  <div className='img'>{item.photo && <img src={item.cover} alt='' />}</div>*/}
              <div className='img'>{item.photo && <img src={PublicFlo + item.photo} alt='' />}</div>
              <div className='details'>
                <div className='tag'>
                  <AiOutlineTags className='icon' />
                  {item.categories.map((c) => (
                    <a href='/'>#{c.name}</a>
                  ))}
                </div>
                <Link to={`/post/${item._id}`}>
                  <h3>{item.title}</h3>
                </Link>
                <p>{item.desc.slice(0, 180)}...</p>
                <div className={`date ${isLiked ? 'liked' : ''}`}>
                  <AiOutlineClockCircle className='icon' /> <label htmlFor=''>{new Date(item.createdAt).toDateString()}</label>
                  {/* <Likes item={item.likes} /> */}
                  <AiOutlineHeart className={`icon ${isLiked ? 'liked-icon' : ''}`} onClick={() =>  Iteminator(item)} /> <label htmlFor=''> <Likes item={likes} /> _ {item.likes} </label> 
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>
    </>
  )
}
