import React from "react";
import "./colormatrix.scss"

export default function ColorMatrix ({ colors, width})  {
  const colorArray = colors.split(' ');

  const matrixStyles = {
    display: 'grid',
    gridTemplateColumns: `repeat(${width}, 1fr)`,
  };
  
  //console.log(colorArray)

  const handleCopyColors = () => {
    const pixelsContainer = document.getElementById('pixels');
    if (pixelsContainer) {
      pixelsContainer.innerHTML = ''; // Очищаем содержимое div перед копированием
      colorArray.forEach((color, index) => {
        const pixel = document.createElement('div');
        pixel.style.backgroundColor = color;
        pixel.style.height = '1rem'; 
        pixel.style.width = '1rem'
        pixelsContainer.appendChild(pixel);
      });
    }
  };


  return (
    <div id="element">
      <div style={matrixStyles}>
        {colorArray.map((color, index) => (
          <div
            key={index}
            style={{
              backgroundColor: color,
              height: '1rem', 
              width: '1rem',
            }}
          />
        ))}
      </div>
      <button onClick={handleCopyColors} >Apply Colors</button>
    </div>
  );
};
