import React, { useState, useContext, useEffect } from "react"
import "./blog.css"
import { blog } from "../../assets/data/data"
import { AiOutlineTags, AiOutlineClockCircle, AiOutlineComment, AiOutlineShareAlt } from "react-icons/ai"
import { AiOutlineHeart } from "react-icons/ai"
import { Link, useLocation } from "react-router-dom"
import axios from "axios"
import { Context } from "../../context/Context"

export const Card = ({ posts }) => {

  const { user } = useContext(Context)
  const [likes, setLikes] = useState(0);
  const [isLiked, setIsLiked] = useState(false);

  const location = useLocation()
  const path = location.pathname.split("/")[2]


  const [post, setPost] = useState({})
  useEffect(() => {
    const getPost = async () => {
      const res = await axios.get("/posts/" + path)
      console.log(res)
      setPost(res.data)
      setLikes(res.data.likes)
    }
    getPost()
  }, [path])

  const handleLikeClick = async () => {
    if (likes % 2 == 0){
      setLikes(likes + 1);
      setIsLiked(true);
    }
    else{
      setLikes(likes - 1);
      setIsLiked(false);
    }
    try {
      await axios.put(`/posts/${post._id}`, { username: user.username, likes})
      //window.location.reload()
    } catch (error) {'hueta', error.message}

  };

  const PublicFlo = "http://localhost:5000/images/"
  return (
    <>
      <section className='blog'>
        <div className='container grid3'>
          {posts.map((item) => (
            <div className='box boxItems' key={item.id}>
              {/* first ma yo  <div className='img'>{item.photo && <img src={item.cover} alt='' />}</div>*/}
              <div className='img'>{item.photo && <img src={PublicFlo + item.photo} alt='' />}</div>
              <div className='details'>
                <div className='tag'>
                  <AiOutlineTags className='icon' />
                  {item.categories.map((c) => (
                    <a href='/'>#{c.name}</a>
                  ))}
                </div>
                <Link to={`/post/${item._id}`}>
                  <h3>{item.title}</h3>
                </Link>
                <p>{item.desc.slice(0, 180)}...</p>
                <div className={`date ${isLiked ? 'liked' : ''}`}>
                  {/* <AiOutlineClockCircle className='icon' /> <label htmlFor=''>{new Date(item.createdAt).toDateString()}</label>
                  <AiOutlineComment className='icon' /> <label htmlFor=''>27</label>*/}
                  <AiOutlineHeart className={`icon ${isLiked ? 'liked-icon' : ''}`} onClick={handleLikeClick} /><label htmlFor=''>{item.likes}</label> 
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>
    </>
  )
}
