import React, { useState, useContext, useEffect } from "react";
import "./blog.css";
import { AiOutlineTags, AiOutlineClockCircle, AiOutlineHeart } from "react-icons/ai";
import { Link, useLocation } from "react-router-dom";
import axios from "axios";
import { Context } from "../../context/Context";

export const Card = ({ posts }) => {
  const { user } = useContext(Context);
  const [isLiked, setIsLiked] = useState(false);

  const { search } = useLocation();

  const handleLikeClick = async (item) => {
    try {
      const response = await axios.put(`/posts/${item._id}`, { username: user.username });
      setIsLiked(!isLiked);
    } catch (error) {
      console.error("Error updating likes", error.message);
    }
  };

  const PublicFlo = "http://localhost:5000/images/";

  useEffect(() => {
    // Fetch updated data after like operation is completed
    const fetchData = async () => {
      try {
        const response = await axios.get(`/posts/${item._id}`);
        setLikes(response.data.likes);
      } catch (error) {
        console.error("Error fetching updated data", error.message);
      }
    };

    fetchData();
  }, [isLiked]); // Trigger the effect when the 'isLiked' state changes

  return (
    <>
      <section className="blog">
        <div className="container grid3">
          {posts.map((item) => (
            <div className="box boxItems" key={item.id}>
              <div className="img">{item.photo && <img src={PublicFlo + item.photo} alt="" />}</div>
              <div className="details">
                <div className="tag">
                  <AiOutlineTags className="icon" />
                  {item.categories.map((c) => (
                    <a href="/" key={c.name}>
                      #{c.name}
                    </a>
                  ))}
                </div>
                <Link to={`/post/${item._id}`}>
                  <h3>{item.title}</h3>
                </Link>
                <p>{item.desc.slice(0, 180)}...</p>
                <div className={`date ${isLiked ? "liked" : ""}`}>
                  <AiOutlineClockCircle className="icon" />
                  <label htmlFor="">{new Date(item.createdAt).toDateString()}</label>
                  <AiOutlineHeart className={`icon ${isLiked ? "liked-icon" : ""}`} onClick={() => handleLikeClick(item)} />
                  {item.likes}
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>
    </>
  );
};
