import React from "react";
import "./create.css";

export const Choose = ({ photo, onSelectFile }) => {
  const handleFileSelection = (e) => {
    const selectedFile = e.target.files[0];
    onSelectFile(selectedFile, photo._id);
  };

  return (
    <>
      <div className="choosen">
        <img key={photo._id} src={photo.picture} alt={`pixel`} />
        <input
          type="file"
          id={`inputfile-${photo._id}`}
          style={{ display: "none" }}
          onChange={handleFileSelection}
        />
        <label htmlFor={`inputfile-${photo._id}`} className="button">
          Choose File
        </label>
      </div>
    </>
  );
};
