import React, { useState, useContext, useEffect } from "react";
import "./blog.css";
import { AiOutlineTags, AiOutlineClockCircle, AiOutlineHeart } from "react-icons/ai";
import { Link, useLocation } from "react-router-dom";
import axios from "axios";
import { Context } from "../../context/Context";

export const Card = ({ posts }) => {
  const { user } = useContext(Context);
  const { search } = useLocation();

  const [likesMap, setLikesMap] = useState({});

  useEffect(() => {
    // Fetch initial likes for all posts
    const fetchLikes = async () => {
      const likesData = await Promise.all(
        posts.map(async (item) => {
          const response = await axios.get(`/posts/${item._id}/likes`);
          return { postId: item._id, likes: response.data.likes };
        })
      );

      const likesObject = {};
      likesData.forEach((data) => {
        likesObject[data.postId] = data.likes;
      });

      setLikesMap(likesObject);
    };

    fetchLikes();
  }, [posts]);

  const handleLikeClick = async (item) => {
    try {
      const updatedLikes = isLiked ? likesMap[item._id] - 1 : likesMap[item._id] + 1;

      await axios.put(`/posts/${item._id}`, { username: user.username, likes: updatedLikes });

      // Update local state with the new likes count
      setLikesMap((prevLikesMap) => ({ ...prevLikesMap, [item._id]: updatedLikes }));
    } catch (error) {
      console.error("Error updating likes", error.message);
    }
  };

  const PublicFlo = "http://localhost:5000/images/";

  return (
    <>
      <section className="blog">
        <div className="container grid3">
          {posts.map((item) => (
            <div className="box boxItems" key={item.id}>
              <div className="img">{item.photo && <img src={PublicFlo + item.photo} alt="" />}</div>
              <div className="details">
                <div className="tag">
                  <AiOutlineTags className="icon" />
                  {item.categories.map((c) => (
                    <a href="/" key={c.name}>
                      #{c.name}
                    </a>
                  ))}
                </div>
                <Link to={`/post/${item._id}`}>
                  <h3>{item.title}</h3>
                </Link>
                <p>{item.desc.slice(0, 180)}...</p>
                <div className={`date ${likesMap[item._id] ? 'liked' : ''}`}>
                  <AiOutlineClockCircle className="icon" />{" "}
                  <label htmlFor="">{new Date(item.createdAt).toDateString()}</label>
                  <AiOutlineHeart
                    className={`icon ${likesMap[item._id] ? 'liked-icon' : ''}`}
                    onClick={() => handleLikeClick(item)}
                  />{" "}
                  {likesMap[item._id] || item.likes}
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>
    </>
  );
};
