import React from "react";
import axios from "axios";

const POLL_INTERVAL = 5000; // Set your desired polling interval in milliseconds

export const startLikesPolling = async (postId, onUpdate) => {
  const pollLikes = async () => {
    try {
      const response = await axios.get(`/posts/${postId}`);
      const updatedLikes = response.data.likes;
      onUpdate(updatedLikes);
    } catch (error) {
      console.error("Error polling likes:", error.message);
    }
  };

  // Start the initial poll
  pollLikes();

  // Set up the interval for polling
  setInterval(pollLikes, POLL_INTERVAL);
};
